$(document).ready((function(){fetch("/mocks/summary.json").then(t=>t.json()).then(renderSummary),fetch("/mocks/all-tickets.json").then(t=>t.json()).then(renderAllTickets),fetch("/mocks/unresolved-tickets.json").then(t=>t.json()).then(renderUnresolvedTickets),fetch("/mocks/tasks.json").then(t=>t.json()).then(renderTasks)})),$(".menu__tabs .menu__tab").on("click",(function(t){t.preventDefault(),$(this).siblings().removeClass("menu__tab_active"),$(this).addClass("menu__tab_active");let s=$(this).attr("data-page-id");$(s).addClass("content__page_active"),$(".content > .content__page").not(s).removeClass("content__page_active")}));const renderSummary=({summaryCards:t})=>{const s=$(".summary__cards");t.map((t,e)=>{s.append(`\n    <div class="summary__cards--card card">\n        <div class="summary__cards--title">${t.name}</div>\n        <div class="summary__cards--amount">${t.amount}</div>\n      </div>\n    `)})},renderAllTickets=({tickets:t})=>{const s=$(".all-tickets__tickets");t.map((t,e)=>{new Date(t.deadlineDate);s.append(`\n      <div class="all-tickets__ticket">\n        <div class="all-tickets__ticket-details">\n            <div class="all-tickets__ticket-photo">\n                <img class="all-tickets__ticket-image" alt="" src="${t.photoUrl}"/>\n            </div>\n            <div class="all-tickets__ticket-info">\n              <div class="all-tickets__ticket-title">${t.title}</div>\n              <div class="all-tickets__last-update">Updated ${t.lastUpdate}</div>\n            </div>\n        </div>\n         <div class="all-tickets__customer">\n            <div class="all-tickets__customer-name">${t.customerName} ${t.customerSurname}</div>\n            <div class="all-tickets__created-date">on ${t.createdDate}</div>\n         </div>\n         <div class="all-tickets__date-time">\n            <div class="all-tickets__date">${moment(t.deadlineDate).format("MMM DD, YYYY")}</div>\n            <div class="all-tickets__time">${moment(t.deadlineDate).format("LT")}</div>\n         </div>\n         <div class="all-tickets__priority all-tickets__priority-${t.priority}">${t.priority}</div>\n         <div class="all-tickets__more-button">\n            <i class="all-tickets__button-icon fas fa-ellipsis-v"></i>\n         </div>\n      </div>\n      <div class="all-tickets__ticket-border"></div>\n    `)})},renderUnresolvedTickets=({unresolvedTickets:t})=>{const s=$(".unresolved-tickets__items");t.map((t,e)=>{s.append(`\n    <div class="unresolved-tickets__item">\n      <div class="unresolved-tickets__item-title">${t.name}</div>\n      <div class="unresolved-tickets__item-count">${t.count}</div>\n    </div>\n    <div class="unresolved-tickets__item-border"></div>\n    `)})},renderTasks=({tasks:t})=>{const s=$(".tasks__tasks-list");t.map((t,e)=>{let a=!0===t.completed?"checked":"";s.append('\n      <div class="tasks__list-border"></div>\n      <div class="tasks__task task">\n        <input type="checkbox" class="task__checkbox" '+a+`>\n        <div class="task__title">${t.title}</div>\n        <div class="task__status task__status-${t.status}">${t.status}</div>\n      </div>\n    `)})};